(()=>{var d=document.querySelectorAll(".hiddenLeft"),c=document.querySelectorAll(".hiddenRight"),l=document.querySelectorAll(".hiddenUp"),s=new IntersectionObserver(e=>{e.forEach(o=>{o.isIntersecting?o.target.classList.add("show"):o.target.classList.remove("show")})});d.forEach(e=>s.observe(e));c.forEach(e=>s.observe(e));l.forEach(e=>s.observe(e));var a=document.getElementById("openModalBtn"),i=document.getElementById("closeModalBtn"),t=document.getElementById("modalOverlay");a.addEventListener("click",()=>{t.classList.add("show")});i.addEventListener("click",()=>{t.classList.remove("show")});t.addEventListener("click",e=>{e.target===t&&t.classList.remove("show")});var n=document.getElementById("backToTop");window.addEventListener("scroll",()=>{window.scrollY>300?n.classList.add("show"):n.classList.remove("show")});n.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})});document.addEventListener("DOMContentLoaded",function(){document.body.classList.add("fade-in")});})();
