(()=>{var s=document.getElementById("openModalBtn"),n=document.getElementById("closeModalBtn"),e=document.getElementById("modalOverlay");s.addEventListener("click",()=>{e.classList.add("show")});n.addEventListener("click",()=>{e.classList.remove("show")});e.addEventListener("click",t=>{t.target===e&&e.classList.remove("show")});var o=document.getElementById("backToTop");window.addEventListener("scroll",()=>{window.scrollY>300?o.classList.add("show"):o.classList.remove("show")});o.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})});})();
